(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let s;const r=()=>{const e=new Date("02 november 2021").getTime()-(new Date).getTime();let t=Math.floor(e/1e3%60),n=Math.floor(e/1e3/60%60);return{hours:Math.floor(e/1e3/60/60),minutes:n,seconds:t,remainedSeconds:e}};s=r();let m=()=>{s=r();let e=s.seconds,m=s.minutes,c=s.hours;const d=e=>(e<=9&&(e="0"+e),e);t.textContent=d(c),n.textContent=d(m),o.textContent=d(e)};s.remainedSeconds<0?(clearInterval(m),t.textContent="00",n.textContent="00",o.textContent="00"):setInterval(m,1e3)})()})();